mapper.select:
SELECT id_follow
FROM acorn_follow
WHERE id_follower=#{id}

SELECT tag
FROM acorn_tag
WHERE num_board = #{num_board}

SELECT orgfilename
FROM acorn_picture
WHERE num_board = #{num_board}

SELECT id_like
FROM acorn_like
WHERE num_board = #{num_board} AND id_like = #{id}

SELECT id_bookmark
FROM acorn_bookmark
WHERE num_board = #{num_board} AND id_bookmark = #{id}

SELECT orgfilename AS thumbnail
FROM acorn_user
WHERE id=#{id_writer}

SELECT id_writer AS id_comment_writer, content AS comments, id_target AS id_comment_target
FROM acorn_comment
WHERE num_board=#{num_board}
