mapper.select:
SELECT a.nick, a.name, a.id, b.count_follow, a.orgfilename FROM ( SELECT id_follow, count(id_follow) AS count_follow FROM acorn_follow GROUP BY id_follow ) b, ( SELECT nick, name, id, orgfilename FROM acorn_user
WHERE nick LIKE '%'||#{keyword}||'%' OR name LIKE '%'||#{keyword}||'%'
) a WHERE b.id_follow(+) = a.id

SELECT a.nick, a.name, b.id_writer as id, b.count_tag as count, a.orgfilename FROM ( SELECT id_writer, count(id_writer) AS count_tag FROM ( SELECT b.id_writer FROM ( SELECT num_board, tag FROM acorn_tag
WHERE tag LIKE '%'||#{keyword}||'%'
) t, acorn_board b WHERE b.num_board = t.num_board ) GROUP BY id_writer ) b, acorn_user a WHERE b.id_writer=a.id ORDER BY count_tag DESC

SELECT a.nick, a.name, a.id, b.count_follow, a.orgfilename FROM ( SELECT id_follow, count(id_follow) AS count_follow FROM acorn_follow GROUP BY id_follow ) b, ( SELECT nick, name, id, orgfilename FROM acorn_user
WHERE nick LIKE '%'||#{keyword}||'%' OR name LIKE '%'||#{keyword}||'%'
) a WHERE b.id_follow = a.id

SELECT count(id_follower)
FROM acorn_follow 
WHERE id_follower=#{id} AND id_follow=#{id_follow}
